import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const fetchValue = useRef();
  // console.log(fetchValue);

  const [enteredName, setEnterednName] = useState("");
  // optional -  const [enteredNameIsValid, setEnterednNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredNameTouched] = useState();

  // email state
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState();

  // use one more state to check if the form is valid
  // if one field is not correct means it is invalid
  //const [formIsValid, setFormIsValid] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== "";
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;
  let formIsValid = false;
  if (enteredNameIsValid) {
    formIsValid = true;
  }

  // on Input Name handler
  const nameInputChangeHandler = (event) => {
    setEnterednName(event.target.value);
  };
  const nameInputBlurHandler = (event) => {
    setEnteredNameTouched(true);
  };

  // on Email handler
  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const emailInputBlureHandler = (event) => {
    setEnteredEmailTouched(true);
  };


  const emailFormat = false;
  const enteredEmailIsSpaceFree = enteredEmail.trim() !== "";
  const mailFormat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
  const validateEmail = (event) => {
    if (event.target.value.match(mailFormat)) {
      emailFormat = true;
    }
  };

  const emailInputisRight = emailFormat && enteredEmailIsSpaceFree;

  // form- submission
  const formSubmissionHandler = (event) => {
    event.preventDefault();
    setEnteredNameTouched(true);
    setEnteredEmailTouched(true);

    if (!enteredNameIsValid && !enteredEmailIsSpaceFree) {
      return;
    }

    setEnterednName("");
    setEnteredNameTouched(false);

    //setEnterednNameIsValid(true);
    // useref
    // const enteredValue = fetchValue.current.value;
    // console.log(enteredValue);
  };

  const nameInputClasses = nameInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          ref={fetchValue}
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && (
          <p className="error-text">Name must not be empty</p>
        )}
      </div>

      {/* email INPUT*/}

      <div className={nameInputClasses}>
        <label htmlFor="name">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
        {emailInputisRight && (
          <p className="error-text">Email must not be empty</p>
        )}
      </div>

      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
