import useInput from "../hooks/use-input";

const BasicForm = (props) => {

  const {
    value: enteredNameValue,
    isValid: NameIsValid,
    hasError: firstNameError,
    valueChangeHandler: firstvalueChangeHandler,
    inputBlurHandler: firstvalueBlurHandler,
    reset : resetFirstNameInput,

  } =  useInput( value => value.trim() !=="");

  const {
    value: enteredLastNameValue,
    isValid: lastNameIsValid,
    hasError: lastNameError,
    valueChangeHandler: lastvalueChangeHandler,
    inputBlurHandler: lastvalueBlurHandler,
    reset : resetLastNameInput,
  } =  useInput((value) => value.trim() !=="");



  const {
    value: enteredEmailValue,
    isValid: emailIsValid,
    hasError: emailError,
    valueChangeHandler: emailvalueChangeHandler,
    inputBlurHandler: emailvalueBlurHandler,
    reset : resetEmailInput,
  } =  useInput((value)=> value.trim() !==""); 

  const formHandler = (event)=>{
   event.preventDeafult();



  }





  return (
    <form onSubmit={formHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={firstvalueChangeHandler}
            onBlur={firstvalueBlurHandler}
            value={enteredNameValue}
          />
          {!firstNameError && <p> Name should not be empty and valid</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lastvalueChangeHandler}
            onBlur={lastvalueBlurHandler}
            value={enteredLastNameValue} />
            {!lastNameError && <p> Name should not be empty and valid</p>}
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' onChange={emailvalueChangeHandler}
            onBlur={emailvalueBlurHandler}
            value={enteredEmailValue}/>
            {!emailError && <p> Name should not be empty and valid</p>}
      </div>
      <div className='form-actions'>
        <button disabled={!formisValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
