import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const fetchValue = useRef();
  // console.log(fetchValue);
  const [enteredName, setEnterednName] = useState("");
  const [enteredNameIsValid, setEnterednNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredNameTouched] = useState();

  const nameInputChangeHandler = (event) => {
    setEnterednName(event.target.value);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setEnteredNameTouched(true);

    if (enteredName.trim() === "") {
      setEnterednNameIsValid(false);
      return;
    }

    setEnterednNameIsValid(true);

    const enteredValue = fetchValue.current.value;
    // console.log(enteredValue);
  };


  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;
  const nameInputClasses = nameInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          ref={fetchValue}
          onChange={nameInputChangeHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && (
          <p className="error-text">Name must not be empty</p>
        )}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
